package com.pengnian.lc459;

import org.junit.jupiter.api.Test;

/**
 * @author Zhang Pengnian
 * @since 2019-05-13 21:38
 */
class Solution {

    @Test
    void test() {
        String s = "";
        System.out.println(repeatedSubstringPattern(s));
    }

    private boolean repeatedSubstringPattern(String s) {

        char[] c = s.toCharArray();
        String sub = "";
        for (int i = 0; i < s.length(); i++) {
            sub = sub + c[i];
            String tmpString = "";
            if (s.length() % sub.length() == 0 && s.length() != sub.length() && c[i + 1] == c[0]) {
                int n = s.length() / sub.length();
                while (n != 0) {
                    tmpString = tmpString + sub;
                    n--;
                }
            }
            if (tmpString.equals(s))
                return true;
        }
        return false;

    }
}
